$(function(){var a=location.pathname;a=a.substring($("body").data("locale-root"),a.length);a=a.replace(/\/*$/,"");$.ajax({url:$("body").data("site-root")+"_tracking",type:"POST",data:{url:a,type:"page"},timeout:300});$("a.resource-url-analytics").click(function(c){var b=$(c.target).closest("a").attr("href");$.ajax({url:$("body").data("site-root")+"_tracking",data:{url:b,type:"resource"},type:"POST",complete:function(){location.href=b},timeout:30});c.preventDefault()})});